--
-- Структура таблиці `serials`
--

CREATE TABLE IF NOT EXISTS `serials` (
  `serial_id` int(5) NOT NULL AUTO_INCREMENT,
  `serial_name` varchar(150) NOT NULL,
  `serial_img` varchar(250) NOT NULL,
  `serial_description` text NOT NULL,
  `serial_trailler_id` int(11) NOT NULL,
  `serial_genre` varchar(250) NOT NULL,
  `serial_period` varchar(25) NOT NULL,
  `serial_country_id` int(11) NOT NULL,
  `serial_producer` varchar(250) NOT NULL,
  `serial_cast` text NOT NULL,
  PRIMARY KEY (`serial_id`),
  KEY `serial_country` (`serial_country_id`),
  KEY `serial_country_2` (`serial_country_id`),
  KEY `serial_trailler_id` (`serial_trailler_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=13 ;

--
-- Дамп даних таблиці `serials`
--

INSERT INTO `serials` (`serial_id`, `serial_name`, `serial_img`, `serial_description`, `serial_trailler_id`, `serial_genre`, `serial_period`, `serial_country_id`, `serial_producer`, `serial_cast`) VALUES
(1, 'Чёрный список', 'uploads/tv/blacklist.jpg', '<p><span style="color:rgb(77, 77, 77)">На протяжении десятилетий бывший правительственный агент Рэймонд Реддингтон был одним из самых разыскиваемых беглецов ФБР. Неожиданно он решает сдаться&hellip; На допросе он заявляет, что собирается помочь Бюро с арестом опаснейших &laquo;криминальных умов&raquo;, но сотрудничать будет только с агентом Элизабет Кин, только что принятой в ряды ФБР, объясняя это тем, что Кин &mdash; &laquo;особенная и очень умная&raquo;.</span></p>\r\n', 5, 'Боевик, Триллер, Криминал, Детектив', '2013-2016+', 1, ' Майкл В. Уоткинс, Джо Карнахан, Винс Мисиано ...', '<p>Джеймс Спэйдер, Меган Бун,&nbsp; Диего Клаттенхофф, Гарри Дж. Ленникс,&nbsp;Райан Игголд, Парминдер К. Награ, Хишам Тафик, Амир Арисон, Дебора С. Крэйг, Грем Малкольм, Дженнифер Ким, Чарльз Бэйкер ...</p>\r\n'),
(2, 'Сверхъестественное ', 'uploads/tv/supernatural.jpg', '<p>Сериал рассказывает о&nbsp;приключениях братьев Сэма и&nbsp;Дина Винчестеров, которые путешествуют по&nbsp;Соединённым Штатам на&nbsp;чёрном автомобиле Chevrolet Impala 1967 года, расследуют паранормальные явления, многие из&nbsp;которых основаны на&nbsp;американских городских легендах и&nbsp;фольклоре, и&nbsp;сражаются с&nbsp;порождениями зла, такими как&nbsp;демоны и&nbsp;призраки.</p>\r\n', 1, 'Ужасы, Фэнтези, Триллер, Драма, Детектив.', '2005 - 2016+', 1, ' Philip Sgriccia, Роберт Сингер, Ким Мэннерс ...', '<p>Джаред Падалеки, Джексен Эклс, Джим Бивер, Марк Шеппард, Миша Коллинз, Марк Пеллегрино,&nbsp;&nbsp;Женевьев Падалеки, Кэти Кэссиди, Джеймс Патрик Стюарт,Саманта Феррис, Алона Тал, Джеффри Дин Морган, Осрик Чау, Синди Сэмпсон, Митч Пилледжи, Курт Фуллер, Тай Олссон, Саманта Смит, Николас Элиа, Лиэн Балабан ...</p>\r\n'),
(3, 'Ходячие мертвецы', 'uploads/tv/the_walking_dead.jpg', '<p>Сериал рассказывает историю жизни семьи шерифа после того, как&nbsp;&laquo;зомби&raquo;&nbsp;&mdash;&nbsp;эпидемия апокалиптических масштабов захлестнула земной шар. Шериф Рик&nbsp;Граймс путешествует со&nbsp;своей семьей и&nbsp;небольшой группой выживших в&nbsp;поисках безопасного места для&nbsp;жизни. Но&nbsp;постоянный страх смерти каждый день приносит тяжелые потери, заставляя героев почувствовать глубины человеческой жестокости. Рик&nbsp;пытается спасти свою семью, и&nbsp;открывает для&nbsp;себя, что&nbsp;всепоглощающий страх тех, кто&nbsp;выжил, может быть опаснее бессмысленных мертвецов, бродящих по&nbsp;земле.</p>\r\n', 2, 'Ужасы, Триллер, Драма.', '2010 - 2016+', 1, ' Эрнест Р. Дикерсон, Грег Никотеро, Гай Ферленд ...', '<p>Эндрю Линкольн, Стивен Ян, Чендлер Риггз,Норман Ридус, Мелисса Сюзанн МакБрайд, Лорен Кохэн,Эмили Кинни, Скотт Уилсон, Сара Уэйн Коллис, Лори Холден, Данай Джекесай Гурира, Чад Коулмэн, Сонекуа Мартин-Грин, Джон Бернтал, Айрон Э. Синглтон, Джеффри ДеМанн, Дэвид Морисси, Майкл Рукер, Ларри Джиллиард мл, Хосе Пабло Кантилльо ...</p>\r\n'),
(4, 'Викинги', 'uploads/tv/viking.jpg', '<p><span style="color:rgb(77, 77, 77)">История повествует о приключениях величайшего героя своего времени &ndash; Рагнара Лодброка. В сериале показан Рагнар со своим племенем братьев-викингов и его семьёй в процессе становления королём всех племён викингов. Рагнар &ndash; бесстрашный воин, и религиозный викинг, который объединяет все племена и распространяет среди них поклонение скандинавским богам. Согласно легенде, он &ndash; прямой потомок Одина, бога войны и воителей.</span></p>\r\n', 4, 'Боевик, Драма, Военный, Исторический.', '2013 - 2016+', 3, 'Киаран Доннелли, Кен Джиротти, Йохан Ренк ...', '<p>Трэвис Фиммел, Клайв Стэнден, Кэтрин Уинник, Джессалин Гилсиг, Густаф Скарсгард, Джордж Благден, Тайг Мерфи, Джефферсон Холл, Донал Лог, Гэбриел Бирн, Мод Хирст, Дэвид Пирс, Алисса Сазерленд, Дайармейд Мерта, Александр Людвиг, Лайнес Роуч, Джон Кэвэна, Нэйтан О&rsquo;Тул,&nbsp;Эдвин Эндре ...</p>\r\n'),
(5, 'Древние', 'uploads/tv/the_originals.jpg', '<p>Ребекка, Клаус и Элайджа в 20х годах активно участвовали в жизни города Новый Орлеан. Они тайно управляли его жителями, сделав его очень удобным для жизни сверхъестественных существ. Здесь, в городе клубов и алкоголя их существование превратилось в сплошное веселье. Однако, внезапное появление Майкла лишило их стабильности. Теперь, спустя годы, Древние вампиры вновь возвращаются в город, где держится уникальный баланс сил между вампирами, ведьмами и оборотнями, однако видимое спокойствие таит многие интриги.</p>\r\n', 3, 'Драма, Ужасы, Фэнтези, Детектив.', '2013 - 2016+', 1, ' Крис Грисмер, Джесси Уарн, Мэттью Хатингс ...', '<p>Джозеф Морган, Дэниэл Гиллис, Фиби Тонкин,Чарльз Майкл Дэвис, Леа Пайпс, Даниэль Кэмпбелл,Стивен Крюгер, Юсуф Гейтвуд, Клер Холт, Себастьян Роше, Нэйтан Парсонс, Даниэлла Пинеда, Эка Дарвилл,Дэниэл Шарман, Тодд Стэшвик, Элиз Левек, Чейз Коулмэн,Шеннон Кэйн, Riley Voelkel, Ниши Мунши ...</p>\r\n'),
(6, 'Сотня', 'uploads/tv/the_hundread.jpg', '<p>События в&nbsp;сериале начинают разворачиваться по&nbsp;прошествии девяносто семи лет&nbsp;после того как&nbsp;всю цивилизацию уничтожила страшная атомная война.</p>\r\n\r\n<p>Высоко в&nbsp;космосе на&nbsp;орбите Земли летает большой космический корабль, именно здесь находятся те, кто&nbsp;выжил после страшной катастрофы. Они&nbsp;единственные кто&nbsp;пережил этот ужасный катаклизм который устроило человечество. Именно с&nbsp;этого последнего пристанища людей на&nbsp;Землю отправляют космический челнок на&nbsp;котором находятся сто&nbsp;малолетних преступников&hellip;</p>\r\n', 6, 'Фантастика', '2014 - 2016+', 1, 'Дин Уайт, Бхарат Наллури, П.Дж. Пеше ...', '<p>Элиза Тейлор, Мари Авгеропулос, Боб Морли,Томас МакДонелл, Пейдж Турко, Линдси Морган, Генри Йен Кусик, Сачин Сахель, Исайа Вашингтон, Крис Браунинг, Женевьев Бюхнер, Manny Jacinto, Джарод Джозеф, Ричард Хэрмон, Келли Ху, Риз Александр, Девон Бостик, Eli Goree, Алессандро Джулиани, Кристофер Ларкин ...</p>\r\n'),
(7, 'Декстер', 'uploads/tv/dexter.jpg', '<p>Я&nbsp;&mdash;&nbsp;Декстер. Декстер Морган. Я&nbsp;работаю судмедэкспертом в&nbsp;полиции Майами. Я&nbsp;не понимаю любви, мне&nbsp;безразличен секс, и&nbsp;у меня нет&nbsp;чувств. А&nbsp;еще я&nbsp;серийный убийца.</p>\r\n\r\n<p>Мой отец был&nbsp;полицейским и&nbsp;прекрасно обучил меня прятать улики. Обычным гражданам не&nbsp;стоит меня бояться, я&nbsp;убиваю только преступников, подонков, до&nbsp;которых почему-либо не&nbsp;смогла или&nbsp;не захотела добраться полиция. Я&nbsp;убиваю их, аккуратно распиливаю на&nbsp;части и&nbsp;избавляюсь от&nbsp;трупов. Убийство для&nbsp;меня&nbsp;&mdash;&nbsp;творческий акт, искусство.</p>\r\n\r\n<p>Но однажды&hellip; В&nbsp;Майами появляется некто, равный мне. Даже превзошедший меня. Сумевший меня заинтересовать. Предложивший мне&nbsp;своего рода товарищеское состязание. Кто&nbsp;этот загадочный и&nbsp;неуловимый &laquo;икс&raquo;?..</p>\r\n', 7, 'Триллер, Драма, Криминал, Детектив', '2006 - 2013', 1, 'Скотт Бак, Карен Кэмпбелл, Дэниэл Церон ... ', '<p>Майкл С. Холл, Дженнифер Карпентер, Дэвид Зайас, Джеймс Ремар, К.С. Ли, Лорен Велес, Десмонд Харрингтон, Джули Бенц, Кристина Робинсон, Джеффри Пирсон, Престон Бэйли, Айми Гарсиа, Рик Кинг, Дэна Л. Уилсон, Ивонн Страховски, Дэвид Рамси, Кит Кэррэдин,Пабло Сориано, Колин Хэнкс, Джимми Смитс ...</p>\r\n'),
(8, 'Фарго', 'uploads/tv/fargo.jpg', '<p>В январе 2006 года Лорн Малво проезжает через небольшой городок Бемиджи, штат Миннесота, в котором знакомится и оказывает влияние на торговца страховыми полисами Лестера Найгаарда своей жестокостью&hellip;</p>\r\n', 8, 'Драма, Криминал', '2014 - 2016+', 1, 'Адам Бернштейн, Скотт Уинант, Колин Бакси ...', '<p>Билли Боб Торнтон, Мартин Фриман, Эллисон Толман, Колин Хэнкс, Боб Оденкёрк, Оливер Платт, Адам Голдберг, Расселл Харвард, Кит Кэррэдин, Джои Кинг, Кейт Уолш, Джошуа Клоуз, Лайам Грин, Том Мусгрейв, Лори Рэвенсборг, Шон Дойл, Гленн Хоуертон, Джули Энн Эмери, Рейчел Блэнчард, Кигэн-Майкл Ки ...</p>\r\n'),
(9, 'Шерлок', 'uploads/tv/sherlock.jpg', '<p>События разворачиваются в&nbsp;наши дни. Он&nbsp;прошел Афганистан, остался инвалидом. По&nbsp;возвращении в&nbsp;родные края встречается с&nbsp;загадочным, но&nbsp;своеобразным гениальным человеком. Тот&nbsp;в поиске соседа по&nbsp;квартире. Лондон, 2010 год. Происходят необъяснимые убийства. Скотланд-Ярд без&nbsp;понятия, за&nbsp;что хвататься. Существует лишь один человек, который в&nbsp;силах разрешить проблемы и&nbsp;найти ответы на&nbsp;сложные вопросы.</p>\r\n', 9, 'Триллер, Драма, Криминал, Детектив', '2010 - 2016+', 4, 'Пол МакГиган, Коуки Гидройк, Эрос Лин ...', '<p>Бенедикт Камбербэтч, Мартин Фриман, Уна Стаббс, Руперт Грейвз, Лу Брили, Марк Гатисс, Эндрю Скотт, Джонатан Арис, Винетт Робинсон, Аманда Аббингтон, Таня Муди, Лиза МакАллистер, Зои Телфорд, Ясмин Акрам, Дэвид Неллист, Лара Пюльве, Филип Дэвис,Ванда Вентхам, Тимоти Карлтон, Sydney Wade ...</p>\r\n'),
(10, 'Черные паруса', 'uploads/tv/black_sails.jpg', '<p>1715 год. Золотой век&nbsp;&mdash;&nbsp;вершина пиратства в&nbsp;Карибском море. Остров бывшей британской колонии Нью-Провиденс является территорией беззакония, контролируемый самыми известными пиратскими капитанами в&nbsp;истории. Самым грозным из&nbsp;них является капитан Флинт.</p>\r\n\r\n<p>Но британский флот возвращается в&nbsp;эти воды, угрожая уничтожить Флинта и&nbsp;его команду. Движимый глубокими, сложными, даже романтическими мотивами, Флинт становится союзником с&nbsp;грозной Элеонорой Гатри, дочерью местного вора в&nbsp;законе, который превращает добычу пиратов в&nbsp;прибыль. Вместе они&nbsp;создают план, охотясь за&nbsp;высшей наградой, выиграв которую, они&nbsp;остановят захват своего дома и&nbsp;обеспечат их&nbsp;выживание.</p>\r\n\r\n<p>Им противостоит ряд&nbsp;соперников: конкурент капитана, завидующий власти Флинта, отец Элеоноры, чьи&nbsp;амбиции на&nbsp;острове приводят к&nbsp;конфликту с&nbsp;дочерью, и&nbsp;молодой моряк, Джон Сильвер, недавно принятый в&nbsp;экипаж Флинта, которому каким-то образом удается постоянно срывать планы своего капитана.</p>\r\n', 10, 'Драма, Приключения', '2014 - 2016+', 1, 'Нил Маршалл, Сэм Миллер, Т.Дж. Скотт ...', '<p>Тоби Стивенс, Ханна Нью, Люк Арнольд, Зэк Макгоун, Тоби Шмитц, Клара Пэджет, Марк Райан, Хаким Кае-Казим, Лоуренс Джофф, Шон Майкл, Джессика Паркер Кеннеди, Том Хоппер, Луиз Барнс, Jannes Eiselen,Патрик Листер, Андре Джейкобс, Карл Танинг, Джереми Кратчли, Марк Элдеркин, Лэнгли Кирквуд ...</p>\r\n'),
(11, 'Доктор Хаус', 'uploads/tv/houseMD.jpg', '<p>Сериал рассказывает о&nbsp;команде врачей, которые должны правильно поставить диагноз пациенту и&nbsp;спасти его. Возглавляет команду доктор Грегори Хаус, который ходит с&nbsp;тростью после того, как&nbsp;его мышечный инфаркт в&nbsp;правой ноге слишком поздно правильно диагностировали. Как&nbsp;врач Хаус просто гений, но&nbsp;сам не&nbsp;отличается проникновенностью в&nbsp;общении с&nbsp;больными и&nbsp;с удовольствием избегает их, если только есть возможность. Он&nbsp;сам всё&nbsp;время проводит в&nbsp;борьбе с&nbsp;собственной болью, а&nbsp;трость в&nbsp;его руке только подчеркивает его&nbsp;жесткую, ядовитую манеру общения. Порой его&nbsp;поведение можно назвать почти бесчеловечным, и&nbsp;при этом он&nbsp;прекрасный врач, обладающий нетипичным умом и&nbsp;безупречным инстинктом, что&nbsp;снискало ему&nbsp;глубокое уважение. Будучи инфекционистом, он&nbsp;ещё и&nbsp;замечательный диагност, который любит разгадывать медицинские загадки, чтобы спасти кому-то жизнь. Если бы&nbsp;все было по&nbsp;его воле, то&nbsp;Хаус лечил бы&nbsp;больных не&nbsp;выходя из&nbsp;своего кабинета.</p>\r\n', 11, 'Драма, Детектив', '2004 - 2012', 1, 'Грег Яйтанс, Деран Сарафян, Дэвид Стрейтон ...', '<p>Хью Лори, Роберт Шон Леонард, Лиза Эдельштейн, Омар Эппс, Джесси Спенсер, Дженнифер Моррисон, Питер Джекобсон, Оливия Уайлд, Кэлл Пенн,Эмбер Тэмблин, Боббин Бергсторм, Шарлин Йи, Энн Дудек, Дженнифер Кристал, Лиз Бенуа, Стефани Вендитто,Села Уорд, Майкл Уэстон, Рон Перкинс, Эди Гатеги ...</p>\r\n'),
(12, 'Игра престолов', 'uploads/tv/game_of_thrones.jpg', '<p>К концу подходит время благоденствия, и&nbsp;лето, длившееся почти десятилетие, угасает. Вокруг средоточия власти Семи королевств, Железного трона, зреет заговор, и&nbsp;в это&nbsp;непростое время король решает искать поддержки у&nbsp;друга юности Эддарда Старка. В&nbsp;мире, где&nbsp;все&nbsp;&mdash;&nbsp;от короля до&nbsp;наемника&nbsp;&mdash;&nbsp;рвутся к&nbsp;власти, плетут интриги и&nbsp;готовы вонзить нож&nbsp;в спину, есть место и&nbsp;благородству, состраданию и&nbsp;любви. Между тем, никто не&nbsp;замечает пробуждение тьмы из&nbsp;легенд далеко на&nbsp;Севере&nbsp;&mdash;&nbsp;и лишь Стена защищает живых к&nbsp;югу от&nbsp;нее.</p>\r\n', 12, 'Фэнтези, Драма, Приключения', '2011 - 2016+', 1, 'Алан Тейлор, Дэвид Наттер, Алекс Грейвз ...', '<p>Питер Динклэйдж, Лина Хиди, Эмилия Кларк, Кит Харингтон, Николай Костер-Валдау, Мэйси Уильямс, Софи Тернер, Шон Бин, Чарльз Дэнс, Натали Дормер, Элфи Оуэн-Аллен, Эйдан Гиллен, Стивен Диллэйн, Кэрис Ван Хаутен, Иэн Глен, Джек Глисон, Мишель Фэйрли, Ричард Мэдден, Гвендолин Кристи, Натали Эммануель ...</p>\r\n');

